contracts/E8MintableNft.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import '@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155BurnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import '@openzeppelin/contracts/utils/Counters.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';

contract E8MintableNft is
    Initializable,
    ERC1155Upgradeable,
    AccessControlUpgradeable,
    PausableUpgradeable,
    ERC1155BurnableUpgradeable,
    ERC1155SupplyUpgradeable,
    UUPSUpgradeable
{
    using Counters for Counters.Counter;

    bytes32 public constant URI_SETTER_ROLE = keccak256('URI_SETTER_ROLE');
    bytes32 public constant PAUSER_ROLE = keccak256('PAUSER_ROLE');
    bytes32 public constant MINTER_ROLE = keccak256('MINTER_ROLE');
    bytes32 public constant UPGRADER_ROLE = keccak256('UPGRADER_ROLE');

    Counters.Counter private _tokenIdCounter;
    string private baseURI;
    mapping(uint256 => string) private idToUri;

    function getCurrentId() external view returns (uint256) {
        return _tokenIdCounter.current();
    }

    function isExists(uint256 tokenId) external view returns (bool) {
        return exists(tokenId);
    }

    function getBaseURI() external view returns (string memory) {
        return baseURI;
    }

    function tokenURI(uint256 tokenId) external view returns (string memory) {
        return uri(tokenId);
    }

    function batchTokenURI(uint256[] memory tokenIds) external view returns (string[] memory) {
        string[] memory arrUris = new string[](tokenIds.length);
        for (uint256 i = 0; i < tokenIds.length; i++) {
            if (exists(tokenIds[i])) {
                arrUris[i] = uri(tokenIds[i]);
            } else arrUris[i] = '';
        }
        return arrUris;
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function uri(uint256 tokenId) public view override(ERC1155Upgradeable) returns (string memory) {
        require(exists(tokenId), 'E8MintableNft: URI query for nonexistent token');
        return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, idToUri[tokenId])) : '';
    }

    event SetBaseURI(string newBaseURI);
    event SetTokenURI(uint256 tokenId, string newTokenUri);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() initializer {}

    function initialize(address owner) public initializer {
        baseURI = 'https://ipfs.io/ipfs/';
        __ERC1155_init(baseURI);
        __AccessControl_init();
        __Pausable_init();
        __ERC1155Burnable_init();
        __ERC1155Supply_init();
        __UUPSUpgradeable_init();
        _grantRole(DEFAULT_ADMIN_ROLE, owner);
        _grantRole(URI_SETTER_ROLE, owner);
        _grantRole(PAUSER_ROLE, owner);
        _grantRole(MINTER_ROLE, owner);
        _grantRole(UPGRADER_ROLE, owner);
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyRole(UPGRADER_ROLE) {}

    function mint(
        address account,
        uint256 amount,
        string memory tokenUri,
        bytes memory data
    ) external onlyRole(MINTER_ROLE) {
        uint256 id = _tokenIdCounter.current();
        require(!exists(id), 'E8MintableNft: one of tokens is existing');
        _mint(account, id, amount, data);
        setTokenURI(id, tokenUri);
        _tokenIdCounter.increment();
    }

    function mintBatch(
        address to,
        uint256[] memory amounts,
        string[] memory uris,
        bytes memory data
    ) external onlyRole(MINTER_ROLE) {
        uint256[] memory ids = new uint256[](uris.length);
        for (uint256 i = 0; i < ids.length; i++) {
            uint256 id = _tokenIdCounter.current();
            _tokenIdCounter.increment();
            require(!exists(id), 'E8MintableNft: one of tokens is existing');
            setTokenURI(id, uris[i]);
            ids[i] = id;
        }
        _mintBatch(to, ids, amounts, data);
    }

    function mintExists(
        address account,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) external onlyRole(MINTER_ROLE) {
        require(exists(id), 'E8MintableNft: token dont exest');
        _mint(account, id, amount, data);
    }

    function mintExistsBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) external onlyRole(MINTER_ROLE) {
        for (uint256 i = 0; i < ids.length; i++) {
            require(exists(ids[i]), 'E8MintableNft: one of tokens dont exist');
        }
        _mintBatch(to, ids, amounts, data);
    }

    function setURI(string memory newuri) public onlyRole(URI_SETTER_ROLE) {
        baseURI = newuri;
        _setURI(newuri);
        emit SetBaseURI(newuri);
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function setTokenURI(uint256 tokenId, string memory newTokenUri) public onlyRole(URI_SETTER_ROLE) returns (bool) {
        idToUri[tokenId] = newTokenUri;
        emit SetTokenURI(tokenId, newTokenUri);
        return true;
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override(ERC1155Upgradeable, ERC1155SupplyUpgradeable) whenNotPaused {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }
}
